import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { incrItem, decrItem, removeItem, toggleCart } from "../redux/action";

const Cart = () => {
  const { isCartOpen, cart } = useSelector((state) => state.cartReducer);
  const dispatch = useDispatch();

  const cartTotal = cart
    .map((item) => {
      return item.price * item.quantity;
    })
    .reduce((preVal, curVal) => {
      return preVal + curVal;
    }, 0);

  if (isCartOpen) {
    return (
      <>
        <div id="cart">
          <div className="cart_content">
            <div className="cart_head">
              <h2>
                Cart <small>({cart.length})</small>
              </h2>
              <div
                title="Close"
                className="close_btn"
                onClick={() => dispatch(toggleCart(false))}
              >
                <span>&times;</span>
              </div>
            </div>

            <div className="cart_body">
              {cart.map((item) => {
                const { id, img, title, price, quantity } = item;

                return (
                  <div className="cart_items" key={id}>
                    <figure className="cart_items_img">
                      <img src={img} alt="product-img" />
                    </figure>

                    <div className="cart_items_info">
                      <h4>{title}</h4>
                      <h3 className="price">tk. {price.toLocaleString()}</h3>
                    </div>

                    <div className="cart_items_quantity">
                      <span onClick={() => dispatch(decrItem(id))}>
                        &#8722;
                      </span>
                      <b>{quantity}</b>
                      <span onClick={() => dispatch(incrItem(id))}>&#43;</span>
                    </div>

                    <div
                      title="Remove Item"
                      className="cart_items_delete"
                      onClick={() => dispatch(removeItem(id))}
                    >
                      <span>&times;</span>
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="cart_foot">
              <h3>
                <small>Total:</small>
                <b>tk. {cartTotal.toLocaleString()}</b>
              </h3>
            </div>
          </div>
        </div>
      </>
    );
  }
};

export default Cart;
